package cn.qzl.www.Excetion_14.Sleep;/** * @Author qzl * @Description * sleep方法让当前线程休眠给定毫秒数 * @Since 2020-05-04 10:49 */public class Main {    public static void main(String[] args) {        Runnable t = new Tread_1();        new Thread(t).start();        for (int i = 100; i < 200; i++) {            System.out.println("  "+i);            try {                Thread.sleep(2000);            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }}