package cn.qzl.www.Excetion_14.Sleep;/** * @Author qzl * @Description * @Since 2020-05-04 10:49 */public class Tread_1 implements Runnable{    @Override    public void run(){        for (int i = 0; i < 100; i++) {            System.out.println(i);            try {                Thread.sleep(1000);            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }}