package cn.qzl.www.Excetion_14.ThreadInterruption.demo_1;/** * @Author qzl * @Description * @Since 2020-05-04 18:12 */public class HelloThread extends Thread {    public void run() {        int n = 0;        while (!isInterrupted()) {            n++;            System.out.println(n + " hello!");            try {            Thread.sleep(100);            }            catch (InterruptedException e) {            break;            }        }    }}