package cn.qzl.www.Excetion_14.ThreadSyn;import java.util.Objects;import java.util.Random;/** * @Author qzl * @Description * 同步代码块实现同步 * @Since 2020-05-05 18:04 */public class SynCode implements Runnable {    private int ticket = 100;    Object obj = new Object();    @Override    public void run() {        Random random = new Random();        while (ticket > 0) {            synchronized (obj) {                if (ticket > 0) {                    System.out.println(Thread.currentThread().getName() + "正在出售" + ticket--);                }            }            try {                Thread.sleep(random.nextInt(10));            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }    public static void main(String[] args) {        Runnable r = new SynCode();        Thread t1 = new Thread(r);        Thread t2 = new Thread(r);        Thread t3 = new Thread(r);        t1.start();        t2.start();        t3.start();    }}