package cn.qzl.www.Excetion_14.ThreadSyn;/** * @Author qzl * @Description * 声明一个域为Volatile,那么这个域将会同步更新。 * @Since 2020-05-07 11:37 */public class VolatileTest implements Runnable{    private volatile int ticket = 1000;    @Override    public void run(){        while(true){            if(ticket>0){                System.out.println(Thread.currentThread().getName()+"-->正在售卖"+ticket--);            }        }    }    public static void main(String[] args) {        VolatileTest v = new VolatileTest();        Thread t1 = new Thread(v,"窗口1");        Thread t2 = new Thread(v,"窗口2");        Thread t3 = new Thread(v,"窗口3");        t1.start();        t2.start();        t3.start();    }}