package cn.qzl.www.Excetion_14.demo_1_Product;/** * @Author qzl * @Description * 消费者 * @Since 2020-05-07 18:20 */public class Consumer implements Runnable{    private Warehouse warehouse;    public Consumer(Warehouse warehouse) {        this.warehouse = warehouse;    }    @Override    public void run(){        int i = 0;        while(i<10){            ++i;            warehouse.pop();        }    }}