package cn.qzl.www.Excetion_14.demo_1_Product;/** * @Author qzl * @Description * 产品 * @Since 2020-05-07 18:10 */public class Product {    private String name;    private static int Id = 1000;    private static int id;    public Product() {        this.name = "电话";        id = Id++;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public  int getid() {        return id;    }    public  void setid() {        id = Id;    }    @Override    public String toString(){        return this.name+" "+this.id;    }}